  #{roomArea,jdbcType=DOUBLE}, #{roomStatus,jdbcType=VARCHAR}, #{roomMark,jdbcType=VARCHAR},
  #{isDeleted,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{lesseeId,jdbcType=VARCHAR}
  )
